create table ingredient (id bigint generated by default as identity, stock bigint, name varchar(255), primary key (id));
create table "order" (datetime_ordered timestamp(6), id bigint generated by default as identity, recipe_id bigint, status varchar(255) check (status in ('AWAITING','FINISHED')), primary key (id));
create table recipe (id bigint generated by default as identity, name varchar(255), primary key (id));
create table recipe_step (id bigint generated by default as identity, ingredient_id bigint, quantity bigint, recipe_id bigint, action_type varchar(255) check (action_type in ('ADD_INGREDIENT','HEAT_WATER','FROTH_MILK','MIX','WAIT','POUR_INTO_CUP')), primary key (id));